internal class NewsMockApi(private val json: Json) : NewsApi {

    override suspend fun getEverythingNews(
        query: String,
        page: Int,
        pageSize: Int
    ): NewsDtoResponse {
        delay(DELAY_REQUEST)
        return if (query.isNotBlank()) {
            json.decodeFromString<NewsDtoResponse>(
                topHeadlinesMap["${NewsCategory.Technology}$page"] ?: ""
            )
        } else NewsDtoResponse(status = "ok", totalResults = 62, articles = listOf())
    }

    override suspend fun getTopHeadlinesNews(
        query: String?,
        category: String,
        page: Int,
        pageSize: Int
    ): NewsDtoResponse {
        return try {
            delay(DELAY_REQUEST)
            json.decodeFromString<NewsDtoResponse>(topHeadlinesMap["$category$page"] ?: "")
        }catch (e: Exception){
            NewsDtoResponse(status = "ok", totalResults = 62, articles = listOf())
        }
    }

